import React, {useState} from "react";

function App() {

  const [list, setList] = useState([]);
  const [input, setInput] = useState("");

  const addItem = (item) => {
    if (item === "") return;
    const newItem = { // json object
      id: Math.random(), //random key for mapping
      item: item
    };
    
    setList([...list, newItem]); //add item to the list, the '...' means append
    setInput(""); //clear input box
  };

  const deleteItem = (id) => {
    const newList = list.filter((item) => item.id !== id); //create a new list that filters out an item with the input id
    setList(newList);
  }

  return (
    <div>
      <h1><span id="header">Stylish\n</span>Todo<span id="textgradient">List</span></h1>
      <input type="text" 
      value={input} 
      onChange={(e) => setInput(e.target.value)} //listen for input and update setInput
      />
      <button id="addbutton" onClick={() => addItem(input)}>Add</button>
      <ul id="itemcontainer">
        {
          list.map((item) => (
            <ul id="itembox" key={item.id}>
              {item.item}
              <button id="removebutton" onClick={() => deleteItem(item.id)}>&times;</button>
            </ul>
          ))
        }
      </ul>
    </div>
  );
}

export default App;
